var fs = require('fs'),
    path = require("path");
var stat = fs.stat;
// process是一个全局对象， argv返回的是一组包含命令行参数的数组。 第一项为” node”， 第二项为执行的js的完整路径， 后面是附加在命令行后的参数
var arguments = process.argv.splice(2);
console.log(arguments)



// 创建所有目录
var mkdirs = function(dirpath, mode, callback) {
    fs.exists(dirpath, function(exists) {
        if (exists) {
            callback(dirpath);
        } else {
            //尝试创建父目录，然后再创建当前目录
            mkdirs(path.dirname(dirpath), mode, function() {
                fs.mkdir(dirpath, mode, callback);
            });
        }
    });
};

// 在复制目录前需要判断该目录是否存在，不存在需要先创建目录
var exists = function(src, dst, callback) {
    fs.exists(dst, function(exists) {
        // 已存在
        if (exists) {
            callback(src, dst);
        }
        // 不存在
        else {
            mkdirs(dst, '0777', function() {
                callback(src, dst);
            });
        }
    });
};

var getFiles = function(src, dst) {
    // src = './' + src;
    if (!path.isAbsolute(src)) {
        src = path.join(__dirname, '../', src);
    }
    // 读取目录中的所有文件/目录
    fs.readdir(src, function(err, paths) {
        if (err) {
            throw err;
        }
        // console.log('paths', paths);
        paths.forEach(function(_path) {
            var _src = path.join(src, _path),
                _dst = path.join(dst, _path),
                readable, writable;
            stat(_src, function(err, st) {
                if (err) {
                    throw err;
                }
                // 判断是否为文件
                if (st.isFile()) {
                    // 创建读取流
                    readable = fs.createReadStream(_src);
                    // 创建写入流
                    writable = fs.createWriteStream(_dst);
                    // 通过管道来传输流
                    readable.pipe(writable);
                }
                // 如果是目录则递归调用自身
                else if (st.isDirectory()) {
                    exists(_src, _dst, getFiles);
                }
            });
        });
    });

};

if (arguments.length > 0) {
    if (arguments[0] == 'wechat') {
        getFiles(path.join(__dirname, '../static/wechat/assets'), path.join(__dirname, '../../../src/assets'));
        getFiles(path.join(__dirname, '../static/wechat/components'), path.join(__dirname, '../../../src/components'));
        getFiles(path.join(__dirname, '../static/wechat/page'), path.join(__dirname, '../../../src/page'));
    }

}