var msgCount = function(args) {
    let time = args.seconds || 60;

    decrease = function() {
        setTimeout(function() {
            time--;
            if (time > 0) {
                console.log(time);
                decrease(time);
            } else {
                console.log('done');
            }
        }, 1000);
    };
};

