<script>
    import materialSelectDialog from '../Materialselectdialog';
    import photoSelectDialog from '../Photoselectdialog.vue';
    import {Button} from 'element-ui';
    tools.vue.use(Button);
    
    export default{
        data(){
            return {
                msg_type: 1,
                msgTxt: '',
                media_path: '',
                media_id: 0,
                news_ids:[],
                showMaterialDlg: false,
                showPhotoDlg: false,
            }
        },
        computed:{

        },
        methods: {
            getData: function(){
                let that = this;

                tools.alert.loading();
                tools.ajax({
                    url: '/api/admin/wechat/responses/2',
                    ajaxData: {},
                    successFun: function (res) {
                        tools.alert.closeLoading();
                        if(res && res.status){
                            /*that.msg_type = res.data.rsp_type;
                            that.msgTxt = res.data.text ? res.data.text : '';
                            that.media_id = res.data.wechat_media_id;
                            if(res.data.news && res.data.news.length > 0){
                                that.news_ids = res.data.news;
                            }
                            if(that.msgTxt != ''){
                                $('#msgeditor').html(that.msgTxt);
                            }*/

                            for(let i in res.data){
                                switch(res.data[i].rsp_type){
                                    case 1:{
                                        that.msgTxt = res.data[i].text;
                                        if(that.msgTxt != ''){
                                            $('#msgeditor').html(that.msgTxt);
                                        }
                                    };break;
                                    case 2:{
                                        that.news_ids = res.data[i].news;
                                        /*for(let j in res.data[i].news){
                                        }*/
                                    };break;
                                    case 3:{
                                        that.media_path = res.data[i].img_url;
                                        that.media_id = res.data[i].wechat_media_id;
                                    };break;
                                }
                            }
                            if(that.media_path){
                                that.rsp_type = 3;
                            }
                            if(that.news_ids.length > 0){
                                that.rsp_type = 2;
                            }
                            if(that.msgTxt != ''){
                                that.rsp_type = 1;
                            }
                        }
                    },
                    errorFun: function (error_data, status, headers, error_obj) {
                        tools.alert.closeLoading();
                        tools.alert.error(error_data.error_msg);
                    },
                    type: 'GET'
                });

            },
            divBlur: function(){
                let that = this;
                that.msgTxt = $('#msgeditor').html();
            },
            closeMaterialDlg: function(obj){
                let that = this;
                that.showMaterialDlg = false;
                if(obj){
                    that.news_ids.push({
                        news_id: obj.img_arr[0].id,
                        cover: obj.img_arr[0].cover,
                        title: obj.img_arr[0].title,
                        desc: obj.img_arr[0].desc,
                    });
                }
            },
            deleteNews: function(idx){
                let that = this;
                that.news_ids.splice(idx ,1);
            },
            closePhotoDlg: function(obj){
                let that = this;
                that.showPhotoDlg = false;
                if(obj){
                    that.media_id = obj.img_arr[0].id;
                    that.media_path = obj.img_arr[0].path;
                }                
            },
            deleteMedia: function(){
                let that = this;
                that.media_path = '';
                that.media_id = 0;
            },
            save: function(){
                let that = this, ajaxData = {};
                ajaxData.type = 2;
                ajaxData.data = [];

                ajaxData.data.push({
                    rsp_type: 1,
                    text: that.msgTxt
                });
                if(that.media_id > 0){
                    ajaxData.data.push({
                        rsp_type: 3,
                        wechat_media_id: that.media_id
                    });                    
                }

                let tmpNews = [];
                for(let i in that.news_ids){
                    tmpNews.push(that.news_ids[i].news_id);
                }
                ajaxData.data.push({
                    rsp_type: 2,
                    news: tmpNews
                });
                console.log(ajaxData);

                tools.alert.loading();
                tools.ajax({
                    url: '/api/admin/wechat/responses',
                    ajaxData: ajaxData,
                    successFun: function (res) {
                        tools.alert.closeLoading();
                        tools.alert.message('保存成功');
                    },
                    errorFun: function (error_data, status, headers, error_obj) {
                        tools.alert.closeLoading();
                        tools.alert.error(error_data.error_msg);
                    },
                    type: 'POST'
                });


            },
            deleteResponse: function(){
                let that = this;

                tools.alert.loading();
                tools.ajax({
                    url: '/api/admin/wechat/responses/2',
                    ajaxData: {},
                    successFun: function (res) {
                        tools.alert.closeLoading();
                        tools.alert.message('删除成功');
                        that.msgTxt = '';
                        $('#msgeditor').empty();
                        that.media_path = '';
                        that.media_id = 0;
                        that.news_ids = [];
                    },
                    errorFun: function (error_data, status, headers, error_obj) {
                        tools.alert.closeLoading();
                        tools.alert.error(error_data.error_msg);
                    },
                    type: 'DELETE'
                });
            }

        },
        components: {
            materialSelectDialog,
            photoSelectDialog
        },
        filters:{

        },
        watch:{

        },
        mounted(){
            this.getData();

        }
    }
</script>
